import axios from 'axios';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './App.css';
import { getPokemonThunk } from './redux/pokemonReducer';

function App() {
  const pokemons = useSelector((state) => state.pokemon.pokemons);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getPokemonThunk());
    // axios
    //   .get('https://pokeapi.co/api/v2/pokemon/?limit=12')
    //   .then((resp) => resp.data);
  }, []);
  return (
    <div className="wrapper">
      <header className="header">Pokedex</header>

      <div className="app">
        <div className="content">
          {pokemons.map((pokemon) => {
            return (
              <div key={pokemon.url} className="blockCart">
                <img />
                <h2>{pokemon.name}</h2>
                <div>
                  <span>Grass</span>
                  <span>Poison</span>
                </div>
              </div>
            );
          })}

          <div>
            <button className="buttonLoad">Load More</button>
          </div>
        </div>
        <div className="activeBlock">
          <img />
          <h2>Charmander #004</h2>
          <div className="character">
            <span>Type</span>
            <span>Fire</span>
          </div>
          <div className="character">
            <span>Attack</span>
            <span>52</span>
          </div>
          <div className="character">
            <span>Defense</span>
            <span>43</span>
          </div>
          <div className="character">
            <span>HP</span>
            <span>39</span>
          </div>
          <div className="character">
            <span>SP Attack</span>
            <span>60</span>
          </div>
          <div className="character">
            <span>SP Defense</span>
            <span>50</span>
          </div>
          <div className="character">
            <span>Speed</span>
            <span>65</span>
          </div>
          <div className="character">
            <span>Weight</span>
            <span>85</span>
          </div>
          <div className="character">
            <span>Total moves</span>
            <span>89</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
