import axios from 'axios';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './App.css';
import ActiveBlock from './components/ActiveBlock/ActiveBlock';
import LoadMore from './components/LoadMore/LoadMore';
import { getPokemonThunk, getPokemonTypes } from './redux/pokemonReducer';

function App() {
  const pokemons = useSelector((state) => state.pokemon.pokemons);
  const types = useSelector((state) => state.pokemon.types);
  сщтіщдуюдщп(зщлуьщті);
  const dispatch = useDispatch();
  const [activeBlock, setActiveBlock] = useState(false);
  const [selectPokemon, setSelectPokemon] = useState('');
  const [pokemon, setPokemon] = useState(null);

  const loadMorePokemon = () => {
    console.log(pokemon);
    setPokemon(pokemon + 12);
  };

  useEffect(() => {
    dispatch(getPokemonThunk(pokemon));
    dispatch(getPokemonTypes(1));
  }, [pokemon]);

  const selectCard = (pokemon) => {
    setActiveBlock(true);
    setSelectPokemon(pokemon.name);
  };
  return (
    <div className="wrapper">
      <header className="header">Pokedex</header>

      <div className="app">
        <div className="content">
          {pokemons.map((pokemon) => {
            return (
              <div
                onClick={() => {
                  selectCard(pokemon);
                }}
                key={pokemon.url}
                className="blockCart"
              >
                <img src="../img/1.jpg" />
                <h2>{pokemon.name}</h2>
                {(
                  <div>
                    <span>undefined</span>
                  </div>
                ) ||
                  types.map((type) => {
                    return (
                      <div>
                        <span>{type.type.name}</span>
                      </div>
                    );
                  })}
                <div>
                  <span>Grass</span>
                  <span>Poison</span>
                </div>
              </div>
            );
          })}

          <LoadMore loadMorePokemon={loadMorePokemon} />
        </div>
        {activeBlock && <ActiveBlock name={selectPokemon} />}
      </div>
    </div>
  );
}

export default App;
