import { API } from '../api/api';

let initialState = {
  pokemons: [],
};

const pokemonReducer = (state = initialState, action) => {
  switch (action.type) {
    case '': {
      return state;
    }
    default:
      return state;
  }
};

export const getPokemonActionCreator = (pokemons) => ({
  type: 'GET_POKEMON',
  pokemons,
});

export const getPokemonThunk = () => async (dispatch) => {
  let data = await API.getPokemon();
  dispatch(getPokemonActionCreator(data.results));
};
export default pokemonReducer;
