import axios from 'axios';

export const API = {
  getPokemon(pokemon) {
    const response = axios.get(
      `https://pokeapi.co/api/v2/pokemon/?offset=${pokemon}&limit=12`
    );
  },
  selectPokemon(id) {
    return axios
      .get(`https://pokeapi.co/api/v2/pokemon/${id}`)
      .then((resp) => resp.data);
  },
};
