import axios from 'axios';

export const API = {
  getPokemon(pokemon) {
    const response = axios
      .get(`https://pokeapi.co/api/v2/pokemon/?offset=${pokemon}&limit=12`)
      .then((resp) => resp.data);
    // const requestArray = response.data.results.map((request) => {
    //   return axios.get(`https://pokeapi.co/api/v2/pokemon/${request.name}`);
    // });
    // const responseAll = axios.all(requestArray);
    // return responseAll;
  },
  getTypes() {
    return axios
      .get(`https://pokeapi.co/api/v2/type?limit=999`)
      .then((resp) => resp.data);
  },
};
