import axios from 'axios';

export const API = {
  getPokemon(pokemon) {
    return axios
      .get(`https://pokeapi.co/api/v2/pokemon/?offset=${pokemon}&limit=12`)
      .then((resp) => resp.data);
  },
  selectPokemon(id) {
    return axios
      .get(`https://pokeapi.co/api/v2/pokemon/{{${id}}}`)
      .then((resp) => resp.data);
  },
};
