import axios from 'axios';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './App.css';
import ActiveBlock from './components/ActiveBlock/ActiveBlock';
import LoadMore from './components/LoadMore/LoadMore';
import { getPokemonThunk, getPokemonTypes } from './redux/pokemonReducer';

function App() {
  const { pokemons, activeType } = useSelector((state) => state.pokemon);
  //const activeType = useSelector((state) => state.pokemon.types);
  console.log(pokemons);
  const dispatch = useDispatch();
  const [activeBlock, setActiveBlock] = useState(false);
  const [selectPokemon, setSelectPokemon] = useState('');
  const [pokemon, setPokemon] = useState(null);

  const loadMorePokemon = () => {
    console.log(pokemon);
    setPokemon(pokemon + 12);
  };

  useEffect(() => {
    dispatch(getPokemonThunk(pokemon));
    dispatch(getPokemonTypes(1));
  }, [pokemon]);

  const selectCard = (pokemon) => {
    setActiveBlock(true);
    setSelectPokemon(pokemon.name);
  };
  return (
    <div className="wrapper">
      <header className="header">Pokedex</header>

      <div className="app">
        <div className="content">
          {pokemons
            .filter((card) => {
              if (activeType === null) {
                return true;
              } else if (card.types.length > 1) {
                if (
                  card.types[0].type.name === activeType ||
                  card.types[1].type.name === activeType
                ) {
                  return true;
                }
              } else if (card.types.length === 1) {
                if (card.types[0].type.name === activeType) {
                  return true;
                }
              }
              return false;
            })
            .map((card, index) => (
              <div
                onClick={() => {
                  selectCard(card);
                }}
                key={`${card.name + index}`}
                className="blockCart"
              ></div>
            ))}
