import axios from 'axios';
import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './App.css';
import { getPokemonThunk } from './redux/pokemonReducer';

function App() {
  const pokemons = useSelector((state) => state.pokemon.pokemons);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(getPokemonThunk());
    // axios
    //   .get('https://pokeapi.co/api/v2/pokemon/?limit=12')
    //   .then((resp) => resp.data);
  }, []);
  return (
    <div className="wrapper">
      <header className="header">Pokedex</header>

      <div className="app">
        <div className="content">
          {pokemons.map((pokemon) => {
            return (
              <div key={pokemon.url} className="blockCart">
                <img />
                <h2>{pokemon.name}</h2>
                <div>
                  <span>Grass</span>
                  <span>Poison</span>
                </div>
              </div>
            );
          })}

          <div>
            <button className="buttonLoad">Load More</button>
          </div>
        </div>
        <ActiveBlock />
      </div>
    </div>
  );
}

export default App;
