import axios from 'axios';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import './App.css';
import ActiveBlock from './components/ActiveBlock/ActiveBlock';
import LoadMore from './components/LoadMore/LoadMore';
import { getPokemonThunk } from './redux/pokemonReducer';

function App() {
  const pokemons = useSelector((state) => state.pokemon.pokemons);
  const types = useSelector((state) => state.pokemon.types);

  const dispatch = useDispatch();
  const [activeBlock, setActiveBlock] = useState(false);
  const [selectPokemon, setSelectPokemon] = useState('');
  const [pokemon, setPokemon] = useState(null);

  const loadMorePokemon = () => {
    console.log(pokemon);
    setPokemon(pokemon + 12);
    //setSelectPokemon(false);

    //dispatch(getPokemonThunk(setPokemon(pokemon + 12)));
  };

  useEffect(() => {
    dispatch(getPokemonThunk(pokemon));
    axios
      .get(`https://pokeapi.co/api/v2/pokemon/${1}`)
      .then((resp) => resp.data);

    // axios
    //   .get('https://pokeapi.co/api/v2/pokemon/?limit=12')
    //   .then((resp) => resp.data);
  }, [pokemon]);

  const selectCard = (pokemon) => {
    setActiveBlock(true);
    setSelectPokemon(pokemon.name);
  };
  return (
    <div className="wrapper">
      <header className="header">Pokedex</header>

      <div className="app">
        <div className="content">
          {pokemons.map((pokemon) => {
            return (
              <div
                onClick={() => {
                  selectCard(pokemon);
                }}
                key={pokemon.url}
                className="blockCart"
              >
                <img />
                <h2>{pokemon.name}</h2>
                {<div>undefined</div> ||
                  types.map((type) => {
                    return (
                      <div>
                        <span>{type.types.type.name}</span>
                      </div>
                    );
                  })}
                <div>
                  <span>Grass</span>
                  <span>Poison</span>
                </div>
              </div>
            );
          })}

          <LoadMore loadMorePokemon={loadMorePokemon} />
        </div>
        {activeBlock && <ActiveBlock name={selectPokemon} />}
      </div>
    </div>
  );
}

export default App;
